import React, { Component } from 'react'
import '../App.css';

export class Items extends Component {

constructor(props){
   super(props);

   this.state = {
    items: [],
    isLoaded: false,
    search: ''
   }
}

componentDidMount(){
    fetch('https://cors-anywhere.herokuapp.com/https://ace.qtstage.io/api/v1/collections/entertainment')
    .then(res => res.json())
    .then(json => {
        this.setState({
            isLoaded: true,
            items: json.items
        })
    });
}

updateSearch(e){
    this.setState({ search: e.target.value });
}

changeLike = (id)=>{
    console.log(id);
}

    render() {
        var {isLoaded,items,search} = this.state;
        
        const filteredItems = items.filter(item =>{
            return item.story.headline.toLowerCase().includes( search.toLowerCase() )
        })

        if(!isLoaded){
            return (
                <div class="loading">
                    <span>Loading...</span>
                </div>
            )
        }
        else{
            return (
                <div className="items"> 
                    <div className="search-box">
                        <div class="input-group input-group-lg">
                        <div class="input-group-prepend">
                            <span class="input-group-text" id="inputGroup-sizing-lg"><i class="fa fa-search fa-lg" aria-hidden="true"></i></span>
                        </div>
                        <input type="text" class="form-control" placeholder="Search..." value={this.state.search} onChange={this.updateSearch.bind(this)} aria-label="Sizing example input" aria-describedby="inputGroup-sizing-lg" />
                        </div>
                    </div> 

                    <div className="row row-cols-sm-4">
                            {filteredItems.map(item => (
                                <div className="col" key={item.id}>
                                    <div className="card">
                                        <div className="card-header">
                                            <h5 className="card-title">{item.story.headline}</h5>                               
                                        </div>
                                        <div className="card-body">
                                            <p className="card-text">{item.story.slug}</p> 
                                            <p className="card-text text-right">{item.story.status}</p> 
                                        </div>
                                        <div class="card-footer">
                                            <i class="fa fa-heart-o" aria-hidden="true" onClick={this.changeLike(this,item.id)}></i>
                                        </div>        
                                    </div>
                                </div> 
                            ))};  
                    </div>                  
                </div>
            );
        }
        
    }
}

export default Items
