import React, { Component } from 'react'
import '../App.css';

export class Items extends Component {

constructor(props){
   super(props);
   this.state = {
    items: [],
    isLoaded: false,
    isLike: false,
   }
}

changeLike = ()=>{
    this.setState({
        isLike: isLike ? false : true
    })
}

componentDidMount(){
    fetch('https://cors-anywhere.herokuapp.com/https://ace.qtstage.io/api/v1/collections/entertainment')
    .then(res => res.json())
    .then(json => {
        this.setState({
            isLoaded: true,
            items: json.items,
        })
    });
}

    render() {
        var {isLike,isLoaded,items} = this.state;
        if(!isLoaded){
            return <div>Loading...</div>
        }
        else{
            return (
                <div className="items">              
                    <div class="row row-cols-4">
                            {items.map(item => (
                                <div class="col">
                                    <div class="card">
                                        <div class="card-header">
                                            <h5 class="card-title">{item.story.headline}</h5>                               
                                        </div>
                                        <div class="card-body">
                                            <p class="card-text">Some quick example text to build on the card </p> 
                                            <button onClick={this.changeLike}>Like</button>                              
                                        </div>
                                    </div>
                                </div> 
                            ))};  
                    </div>                  
                </div>
            );
        }
        
    }
}

export default Items
