import React, { Component } from 'react'

export class Items extends Component {

constructor(props){
   super(props);
   this.state = {
    items: [],
    isLoaded: false,
   }
}

componentDidMount(){
    fetch('https://ace.qtstage.io/api/v1/collections/entertainment')
    .then(res => res.json())
    .then(json => {
        this.setState({
            isLoaded: true,
            items: json,
        })
    });
}

    render() {
        var {isLoaded,items} = this.state;
        if(!isLoaded){
            return <div>Loading...</div>
        }
        else{
            return (
                <div className="items">
                    data has been loaded!
                </div>
            )
        }
        
    }
}

export default Items
