import React, { Component } from 'react'
import '../App.css';

export class Items extends Component {

constructor(props){
   super(props);

   this.state = {
    items: [],
    isLoaded: false,
   
   }
}

componentDidMount(){
    fetch('https://cors-anywhere.herokuapp.com/https://ace.qtstage.io/api/v1/collections/entertainment')
    .then(res => res.json())
    .then(json => {
        this.setState({
            isLoaded: true,
            items: json.items,
            search: ''
        })
    });
}

updateSearch(e){
    this.setState({ search: e.target.value });
}

changeLike = (id)=>{
    console.log(id);
}

    render() {
        var {isLoaded,items} = this.state;
        items.filter(
            (item) => {
                return item.story.headline.indexOf(this.state.search) !== -1;
            }
        );

        if(!isLoaded){
            return <div>Loading...</div>
        }
        else{
            return (
                <div className="items">  
                <input type="text" value={this.state.search} onChange={this.updateSearch.bind(this)} />            
                    <div className="row row-cols-4">
                            {items.map(item => (
                                <div className="col" key={item.id}>
                                    <div className="card">
                                        <div className="card-header">
                                            <h5 className="card-title">{item.story.headline}</h5>                               
                                        </div>
                                        <div className="card-body">
                                            <p className="card-text">Some quick example text to build on the card </p> 
                                            <button onClick={this.changeLike.bind(this,item.id)}>Like </button>                              
                                        </div>
                                    </div>
                                </div> 
                            ))};  
                    </div>                  
                </div>
            );
        }
        
    }
}

export default Items
